print("Welcome to the Insurance Premium Calculator!")count_private_clients    = 0count_commercial_clients = 0total_private_premium    = []total_commercial_clients = []##Calling againdef call_main():    try:        d =str(input("Another client (y/n) ? "))    except:        call_main()            if( d == 'Y' or d == 'y'):        main()    elif( d == 'N' or d == 'n'):        session_summary()    else:        call_main()        ##Session Summarydef session_summary():    print("")    print("Number of Private clients = ",count_private_clients)    print("Total Private clients premiums collected = ","$"+str(sum(total_private_premium )))    print("Number of Commercial clients = ",count_commercial_clients)    print("Total Commercial clients premiums collected = ","$"+str(sum(total_commercial_clients)))##Domestic/Private Housedef domestic_private_house(amount,claim):    global count_private_clients    count_private_clients = count_private_clients + 1    if(amount < 100000):        a = 0.003 * amount        b = a + 50        if(claim == 0):            c = 0.1 * b            d = b - c            return c,d        else:            return b         elif (amount >= 100000):        a = 0.0025 * amount        b = a + 50        if(claim == 0):            c = 0.1 * b            d = b - c            return c,d        else:            return b##Commercial Premisedef claims_for_commercial_premise(amount):    a = 0.15 * amount    b = amount - a    return bdef commercial_premise(amount,claim):    global count_commercial_clients    count_commercial_clients = count_commercial_clients + 1    if(amount < 250000):        a = 0.005 * amount        b = a + 80        if(claim == 0):            c = 0.15 * b            d = b - c            return c,d        else:            return b         elif (amount >= 250000):        a = 0.0075 * amount        b = a + 80        if(claim == 0):            c = 0.15 * b            d = b - c            return c,d        else:            return b        ##Maindef main():    global total_private_premium    global total_commercial_clients    print("")    a=str(input("User type (P/C)? "))    b=int(input("Amount Insured ($) ? " ))    c=int(input("Number of claims   ? "))    if(a == 'p' or a == 'P'):        result = domestic_private_house(b,c)        if(c != 0):            print(" ")            print("Premium Summary")            print("Amount Insured   = ","$"+str(b))            print("Number of Claims = ",c)            print("Premium = ","$"+str(result))            total_private_premium.append(result)        else:            print(" ")            print("Premium Summary")            print("Amount Insured   = ","$"+str(b))            print("Number of Claims = ",c)            print("No claim Bonus   = ","$"+str(result[0]))            print("Premium = ","$"+str(result[1]))            total_private_premium.append(result[1])                elif(a == 'c' or a == 'C'):        result = commercial_premise(b,c)        if(c != 0):            print(" ")            print("Premium Summary")            print("Amount Insured   = ","$"+str(b))            print("Number of Claims = ",c)            print("Premium = ","$"+str(result))            total_commercial_clients.append(result)        else:            print(" ")            print("Premium Summary")            print("Amount Insured   = ","$"+str(b))            print("Number of Claims = ",c)            print("No claim Bonus   = ","$"+str(result[0]))            print("Premium = ","$"+str(result[1]))            total_commercial_clients.append(result[1])                else:        print("")        print("Invalid Input !! try again")    print("")    call_main()    if __name__ == "__main__":    x=main()